import React from "react";
import Counters from "./counters"


class BoutiqueItem extends React.Component {   
    state = {
        counters: [
        { id: 1, value: 0 },
        ],
    };
    
    
    handleIncrement = (counter) => {
        const counters = [...this.state.counters];
        const index = counters.indexOf(counter);
        counters[index] = { ...counters[index] };
        counters[index].value++;
        this.setState({ counters });
    };
    
    handleDecrement = (counter) => {
        const counters = [...this.state.counters];
        const index = counters.indexOf(counter);
        counters[index] = { ...counters[index] };
        counters[index].value--;
        this.setState({ counters });
    };
    
    
    handleDelete = (counterId) => {
        const counters = this.state.counters.filter((c) => c.id !== counterId);
        this.setState({ counters });
    };
    
    render() {
        const {boutiqueInfo} = this.props;
        return (
            <div className="container-fluid card ml-5 pt-3 pb-3 mb-3">
                <div className="row">
                    <div className="col-12">
                        <div className="container-fluid pb-5">
                            <div className="row">
                                <div className="col-4">
                                    <h3>{boutiqueInfo.nom} </h3>
                                    <h4 className="text-center">{boutiqueInfo.mini} </h4>
                                </div>
                                <div className="col-8">
                                <Counters
                                    onIncrement={this.handleIncrement}
                                    onDecrement={this.handleDecrement}
                                    onDelete={this.handleDelete}
                                    onRestart={this.handleRestart}
                                />
                                </div>                                
                            </div>
                        </div>
                    </div>
                </div>                
            </div>
        )
    }
}

export default BoutiqueItem;
