import React, { Component } from 'react';
import { logout, isLogin } from '../utils';
import { Link } from 'react-router-dom';
import NavBar from './navbar';

class Home extends Component {

    constructor(props) {
        super(props);

        this.state = {
            isLogin: isLogin()
        }
    }

    handleLogout = () => {
        logout();
        this.setState({
            isLogin: false
        })
    }

    render() {
        return (
            <div>                
                {this.state.isLogin ? 
                    <button className="btn-success" onClick={() => this.handleLogout()}>Cliquez pour vous d√©connectez</button>
                    : <Link to="/signin">Se connectez</Link>
                }
            </div>
        );
    }
}



export default Home;
